#!/bin/bash\n\necho \"🚚 TMS Transportation Management System - Deployment Script\"\necho \"============================================================\"\n\n# Check if Node.js is installed\nif ! command -v node &> /dev/null; then\n    echo \"❌ Node.js is not installed. Please install Node.js first.\"\n    exit 1\nfi\n\n# Check if npm is installed\nif ! command -v npm &> /dev/null; then\n    echo \"❌ npm is not installed. Please install npm first.\"\n    exit 1\nfi\n\necho \"✅ Node.js version: $(node --version)\"\necho \"✅ npm version: $(npm --version)\"\necho \"\"\n\n# Install backend dependencies\necho \"📦 Installing backend dependencies...\"\nnpm install\n\nif [ $? -ne 0 ]; then\n    echo \"❌ Failed to install backend dependencies\"\n    exit 1\nfi\n\n# Install frontend dependencies\necho \"📦 Installing frontend dependencies...\"\ncd client\nnpm install\n\nif [ $? -ne 0 ]; then\n    echo \"❌ Failed to install frontend dependencies\"\n    exit 1\nfi\n\ncd ..\n\necho \"✅ All dependencies installed successfully!\"\necho \"\"\necho \"🚀 Starting TMS System...\"\necho \"\"\necho \"📋 Demo Credentials:\"\necho \"   Admin: admin@tms.com / demo123\"\necho \"   Driver: john.driver@tms.com / demo123\"\necho \"\"\necho \"🌐 Access URLs:\"\necho \"   Frontend: http://localhost:3000\"\necho \"   Backend API: http://localhost:5000\"\necho \"\"\necho \"⚡ Features:\"\necho \"   • Real-time GPS tracking with WebSocket\"\necho \"   • Comprehensive trip management\"\necho \"   • Driver management (Admin only)\"\necho \"   • Invoice generation with PDF export\"\necho \"   • Live analytics dashboard\"\necho \"   • Mobile-responsive design\"\necho \"\"\necho \"🔄 Press Ctrl+C to stop the system\"\necho \"============================================================\"\necho \"\"\n\n# Start the application\nnode start.js